struct Display {
// 7-segemnt graphic

	void Setup(const char* const * version) {
		_lcd.begin();
	
	    _lcd.setFontSize(FONT_SIZE_MEDIUM);
		_lcd.setCursor(5, 0);
		_lcd.print(version[0]);
		_lcd.setCursor(5, 3);
		_lcd.print(version[1]);
		_lcd.setFontSize(FONT_SIZE_SMALL);
		_lcd.setCursor(60, 7);
		_lcd.print(version[2]);		
	}

	void Clear() { _lcd.clear(); }
	
	void DrawGroupTemperature(float value) {
		_lcd.setFontSize(FONT_SIZE_MEDIUM);
        _lcd.setCursor(5, 3);
        _lcd.print("Gruppe: ");
        _lcd.print(value, 1);
        _lcd.print("^  ");
	}

	void DrawPreInfusion(int x, int y, int time) {
		_lcd.setFontSize(FONT_SIZE_MEDIUM);
		_lcd.setCursor(x, y);
		_lcd.print("PI: ");
		_lcd.println(time);
	}
	
	void DrawTimer(int value) {
		if (value > 99) value = 99;
		if (value < 0) value = 0;
		
		_lcd.setCursor(66, 1);
		drawDigit(value % 10);
		_lcd.setCursor(30, 1);
		drawDigit((value / 10) % 10);
	}

private:
	void drawDigit(int digit) {
		if (digit > 9 || digit < 0) digit = 0;
		_lcd.draw(digits[digit], 32, 48);
	}
	
private:
	LCD_SSD1306 _lcd;
};

